import React from 'react';
import Image from 'react-bootstrap/Image';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import styled from 'styled-components';
import { NaviBar } from './components/NaviBar';
import backgroundWork from './assets/backgroundWork.jpg';
import { Layout } from './components/Layout';
import Theme from './Theme';
import { TextBlock } from './components/TextBlock';

const Background = styled.div`
        background-image: url(${backgroundWork});
        width: min-vw-100;
        height: min-vh-100;
        background-size: cover;
        position: center;
        background-repeat: no-repeat;
`;

export const Work = () => {
    return (
        <Background>
            <Theme>
                <NaviBar></NaviBar>
                <div class = "container min-wh-100">
                    <div class = "row min-vh-100 justify-content-center align-items-center">
                        <Col>
                            <TextBlock>
                                <h4><b>Fiction</b></h4>
                                <h4><a href = "http://clarkesworldmagazine.com/heyward-rotimi_10_15/">"The Father"</a>: sci-fi short story in <i>Clarkesworld</i>
                                about a man bent on killing a Goddess that he helped to engineer.</h4>
                            </TextBlock>
                        </Col>
                    </div>
                </div>
            </Theme>
        </Background>
    );
};
